---
interface Props {
  title?: string;
  description?: string;
}

const { title = "–õ—ã—Å–∏–Ω–∫–∞ - –∏—Å–∫—É—Å—Å—Ç–≤–æ –±—Ä–∏—Ç–≤—ã", description = "–ü—Ä–µ–æ–±—Ä–∞–∑–∏—Ç–µ —Å–≤–æ–π —Å—Ç–∏–ª—å —Å –ø–æ–º–æ—â—å—é –ø—Ä–æ—Ñ–µ—Å—Å–∏–æ–Ω–∞–ª—å–Ω–æ–≥–æ –±—Ä–∏—Ç—å—è" } = Astro.props;
---

<!doctype html>
<html lang="ru">
  <head>
    <meta charset="UTF-8" />
    <meta name="description" content={description} />
    <meta name="viewport" content="width=device-width, initial-scale=1.0" />
    <link rel="icon" type="image/svg+xml" href="/favicon.svg" />
    <meta name="generator" content={Astro.generator} />
    <title>{title}</title>
    <link rel="preconnect" href="https://fonts.googleapis.com">
    <link rel="preconnect" href="https://fonts.gstatic.com" crossorigin>
    <link href="https://fonts.googleapis.com/css2?family=Inter:wght@300;400;500;600;700;800;900&display=swap" rel="stylesheet">
  </head>
  <body>
    <slot />
  </body>
</html>

<style is:global>
  * {
    margin: 0;
    padding: 0;
    box-sizing: border-box;
  }

  :root {
    --color-primary: #8B7A5D;
    --color-secondary: #9C917B;
    --color-accent: #2D2112;
    --color-background: #ffffff;
    --color-text: #62575B;
    --color-custom-1: #0C100D;
    --color-custom-2: #463C30;
    --color-custom-3: #BB9C86;
    --color-custom-4: #6D5C3F;
    --color-custom-5: #DAB3A1;
    --color-custom-6: #9BA099;
    --color-custom-7: #A97361;

    --font-heading: 'Inter', sans-serif;
    --font-body: 'Inter', sans-serif;

    --size-h1: 3rem;
    --size-h2: 2.5rem;
    --size-h3: 2rem;
    --size-body: 1rem;
  }

  html {
    scroll-behavior: smooth;
  }

  body {
    font-family: var(--font-body);
    font-size: var(--size-body);
    color: var(--color-text);
    background-color: var(--color-background);
    line-height: 1.6;
    overflow-x: hidden;
  }

  h1, h2, h3, h4, h5, h6 {
    font-family: var(--font-heading);
    font-weight: 700;
    line-height: 1.2;
    color: var(--color-accent);
  }

  h1 {
    font-size: var(--size-h1);
  }

  h2 {
    font-size: var(--size-h2);
  }

  h3 {
    font-size: var(--size-h3);
  }

  a {
    color: var(--color-primary);
    text-decoration: none;
    transition: all 0.3s ease;
  }

  a:hover {
    color: var(--color-accent);
  }

  img {
    max-width: 100%;
    height: auto;
    display: block;
  }

  /* Smooth section transitions */
  section {
    position: relative;
    scroll-margin-top: 80px;
  }

  /* Custom scrollbar */
  ::-webkit-scrollbar {
    width: 10px;
  }

  ::-webkit-scrollbar-track {
    background: var(--color-background);
  }

  ::-webkit-scrollbar-thumb {
    background: linear-gradient(to bottom, var(--color-primary), var(--color-secondary));
    border-radius: 5px;
  }

  ::-webkit-scrollbar-thumb:hover {
    background: linear-gradient(to bottom, var(--color-secondary), var(--color-primary));
  }

  @media (max-width: 768px) {
    :root {
      --size-h1: 2.5rem;
      --size-h2: 2rem;
      --size-h3: 1.75rem;
    }
  }
</style>

