      radial-gradient(circle at 25% 25%, rgba(255, 255, 255, 0.05) 1px, transparent 1px),
      radial-gradient(circle at 75% 75%, rgba(255, 255, 255, 0.03) 1px, transparent 1px);
    background-size: 60px 60px, 80px 80px;
    animation: patternMove 25s linear infinite;
  }

  @keyframes patternMove {
    0% {
      transform: translate(0, 0);
    }
    100% {
      transform: translate(60px, 60px);
    }
  }

  .contacts-container {
    max-width: 1200px;
    margin: 0 auto;
    position: relative;
    z-index: 1;
  }

  .section-header {
    text-align: center;
    margin-bottom: 4rem;
    position: relative;
  }

  .section-number {
    display: block;
    font-size: 6rem;
    font-weight: 900;
    color: rgba(255, 255, 255, 0.15);
    line-height: 1;
    margin-bottom: -2rem;
    font-family: var(--font-heading);
  }

  .section-title {
    font-size: clamp(2rem, 5vw, 3rem);
    color: white;
    margin-bottom: 1.5rem;
    position: relative;
    display: inline-block;
  }

  .section-divider {
    width: 100px;
    height: 4px;
    background: linear-gradient(to right, var(--color-custom-3), var(--color-custom-5), var(--color-custom-3));
    margin: 0 auto;
    border-radius: 2px;
    position: relative;
  }

  .section-divider::before,
  .section-divider::after {
    content: '';
    position: absolute;
    top: 50%;
    width: 8px;
    height: 8px;
    background: var(--color-custom-3);
    border-radius: 50%;
    transform: translateY(-50%);
  }

  .section-divider::before {
    left: -15px;
  }

  .section-divider::after {
    right: -15px;
  }

  .contacts-content {
    display: grid;
    grid-template-columns: 1fr 1fr;
    gap: 4rem;
    margin-top: 4rem;
    align-items: center;
  }

  .contact-info {
    display: flex;
    flex-direction: column;
    gap: 2rem;
  }

  .contact-card {
    background: rgba(255, 255, 255, 0.1);
    backdrop-filter: blur(10px);
    padding: 2.5rem;
    border-radius: 20px;
    border: 1px solid rgba(255, 255, 255, 0.2);
    transition: all 0.3s ease;
  }

  .contact-card:hover {
    background: rgba(255, 255, 255, 0.15);
    transform: translateY(-5px);
  }

  .contact-icon-wrapper {
    position: relative;
    display: inline-block;
    margin-bottom: 1.5rem;
  }

  .contact-icon {
    font-size: 2.5rem;
    position: relative;
    z-index: 2;
    filter: drop-shadow(0 4px 8px rgba(0, 0, 0, 0.3));
  }

  .contact-icon-bg {
    position: absolute;
    top: 50%;
    left: 50%;
    transform: translate(-50%, -50%);
    width: 70px;
    height: 70px;
    background: rgba(255, 255, 255, 0.1);
    border-radius: 50%;
    z-index: 1;
    transition: all 0.3s ease;
  }

  .contact-card:hover .contact-icon-bg {
    width: 90px;
    height: 90px;
    background: rgba(255, 255, 255, 0.15);
  }

  .contact-card h3 {
    color: white;
    margin-bottom: 1rem;
    font-size: 1.5rem;
  }

  .contact-card p {
    color: rgba(255, 255, 255, 0.9);
    line-height: 1.8;
    margin-bottom: 0.5rem;
  }

  .contact-note {
    font-size: 0.9rem;
    color: rgba(255, 255, 255, 0.7);
    font-style: italic;
    margin-top: 1rem;
  }

  .contact-cta {
    background: rgba(255, 255, 255, 0.95);
    padding: 3rem;
    border-radius: 24px;
    color: var(--color-text);
    position: relative;
    overflow: hidden;
    backdrop-filter: blur(10px);
    box-shadow: 
      0 20px 60px rgba(0, 0, 0, 0.3),
      0 0 0 1px rgba(255, 255, 255, 0.2);
  }

  .cta-decoration {
    position: absolute;
    top: -50%;
    right: -50%;
    width: 200%;
    height: 200%;
    background: radial-gradient(circle, rgba(139, 122, 93, 0.1) 0%, transparent 70%);
    animation: rotate 20s linear infinite;
  }

  @keyframes rotate {
    0% {
      transform: rotate(0deg);
    }
    100% {
      transform: rotate(360deg);
    }
  }

  .contact-cta h3 {
    color: var(--color-accent);
    margin-bottom: 1rem;
    font-size: 2rem;
  }

  .contact-cta p {
    margin-bottom: 2rem;
    line-height: 1.8;
    color: var(--color-text);
  }

  .contact-cta .cta-button {
    display: inline-flex;
    align-items: center;
    gap: 0.75rem;
    padding: 1.2rem 3rem;
    background: linear-gradient(135deg, var(--color-primary) 0%, var(--color-custom-4) 100%);
    color: white;
    font-weight: 600;
    border-radius: 50px;
    transition: all 0.4s cubic-bezier(0.4, 0, 0.2, 1);
    box-shadow: 
      0 10px 30px rgba(139, 122, 93, 0.4),
      0 0 0 0 rgba(139, 122, 93, 0.5);
    position: relative;
    overflow: hidden;
  }

  .contact-cta .cta-button::before {
    content: '';
    position: absolute;
    top: 50%;
    left: 50%;
    width: 0;
    height: 0;
    border-radius: 50%;
    background: rgba(255, 255, 255, 0.3);
    transform: translate(-50%, -50%);
    transition: width 0.6s, height 0.6s;
  }

  .contact-cta .cta-button:hover::before {
    width: 300px;
    height: 300px;
  }

  .contact-cta .cta-button:hover {
    background: linear-gradient(135deg, var(--color-accent) 0%, var(--color-custom-1) 100%);
    transform: translateY(-3px) scale(1.05);
    box-shadow: 
      0 15px 40px rgba(45, 33, 18, 0.5),
      0 0 0 8px rgba(45, 33, 18, 0.1);
    color: white;
  }

  .contact-cta .cta-button svg {
    transition: transform 0.3s ease;
  }

  .contact-cta .cta-button:hover svg {
    transform: translateX(5px);
  }

  @media (max-width: 968px) {
    .contacts-content {
      grid-template-columns: 1fr;
      gap: 3rem;
    }
  }

  @media (max-width: 768px) {
    .contacts {
      padding: 5rem 1.5rem;
    }

    .contact-cta {
      padding: 2rem;
    }
  }
</style>

